package com.alibaba.mesh.common.serialize;import com.alibaba.mesh.common.URL;import com.alibaba.mesh.common.extension.Adaptive;import com.alibaba.mesh.common.extension.SPI;import java.io.IOException;import java.io.InputStream;import java.io.OutputStream;/** * Serialization. (SPI, Singleton, ThreadSafe) */@SPI("json")public interface Serialization {    /**     * get content type id     *     * @return content type id     */    byte getContentTypeId();    /**     * get content type     *     * @return content type     */    String getContentType();    /**     * create serializer     *     * @param url     * @param output     * @return serializer     * @throws IOException     */    @Adaptive    ObjectOutput serialize(URL url, OutputStream output) throws IOException;    /**     * create deserializer     *     * @param url     * @param input     * @return deserializer     * @throws IOException     */    @Adaptive    ObjectInput deserialize(URL url, InputStream input) throws IOException;}